import React from "react";

function TaskList({ item, deleteTask }) {
  return (
    <li className="Item-List">
      {item.text}
      <span className="icon">
        <i
          className="fa-solid fa-trash"
          onClick={() => deleteTask(item.id)} // Pass the task ID
        ></i>
      </span>
    </li>
  );
}

export default TaskList;
