import { useState } from 'react';
import './App.css';
import TaskInput from './components/TaskInput';
import TaskList from './components/TaskList';

function App() {
  const [search, setSearch] = useState(``);
  const [list, setList] = useState([]);

  const taskList = (inputText) => {
    setList([...list, inputText]);
  };

  const deleteTask = (index) => {
    setList(list.filter((_, i) => i !== index));
  };

  return (
    <div className="App">
      <div className="todo-container">
        <h1 className="heading">Todo App</h1>
        <input
						type="text"
						className="input"
						onChange={(e) => setSearch(e.target.value)}
						placeholder="Search..."
					/>
          <ul>
						{list
							.filter((li) =>
								li.toLowerCase().includes(search.toLowerCase())
							)
							.map((item, key) => (
								<li key={key}>
									{item}{" "}
									<span
										className="delete"
										onClick={() => deleteTask(item)}
									/>
								</li>
							))}
					</ul>

        <TaskInput taskList={taskList} />
        <hr />
        <div className="todo-list">
          {list.map((task, i) => (
            <TaskList key={i} item={task} deleteTask={deleteTask} index={i} />
          ))}
        </div>
        <p>You have {list.length} pending tasks</p>
        {list.length > 0 && (
          <button className="clear-btn" onClick={() => setList([])}>
            Clear All
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
