import { useState } from "react";

const useTaskList = () => {
  const [list, setList] = useState([]);

  const addTask = (task) => {
    setList((prevList) => [...prevList, task]);
  };

  const deleteTask = (index) => {
    setList((prevList) => prevList.filter((_, i) => i !== index));
  };

  const clearTasks = () => {
    setList([]);
  };

  return {
    list,
    addTask,
    deleteTask,
    clearTasks,
  };
};

export default useTaskList;
